package org.pkislov.collectorservice.client;import org.pkislov.collectorservice.config.FeignConfig;import org.pkislov.collectorservice.dto.MessageDto;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.GetMapping;@FeignClient(name = "micro-recipient", url = "${recipient.api.url}", configuration = FeignConfig.class)public interface RecipientClient {    @GetMapping(value = "/message", consumes = "application/json")    MessageDto getMessage();}