package org.pkislov.collectorservice.config;import feign.codec.Decoder;import org.springframework.beans.factory.ObjectFactory;import org.springframework.boot.autoconfigure.http.HttpMessageConverters;import org.springframework.cloud.openfeign.support.SpringDecoder;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class FeignConfig {    private final ObjectFactory<HttpMessageConverters> messageConverters;    public FeignConfig(ObjectFactory<HttpMessageConverters> messageConverters) {        this.messageConverters = messageConverters;    }    @Bean    public Decoder feignDecoder() {        return new SpringDecoder(this.messageConverters);    }}