package org.pkislov.collectorservice.entity;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;import jakarta.persistence.Table;import lombok.Data;@Data@Entity@Table(name = "messages")public class Message {    @Id()    @GeneratedValue(strategy = GenerationType.SEQUENCE)    private long id;    @Column(name = "username")    private String username;    private String message;}