package org.pkislov.collectorservice.service;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.pkislov.collectorservice.client.RecipientClient;import org.pkislov.collectorservice.dto.MessageDto;import org.pkislov.collectorservice.entity.Message;import org.pkislov.collectorservice.repository.MessageRepository;import org.springframework.scheduling.annotation.Scheduled;import org.springframework.stereotype.Service;@Slf4j@Service@RequiredArgsConstructorpublic class MessageCollectorService {    private final RecipientClient recipientClient;    private final MessageRepository messageRepository;    @Scheduled(fixedRateString = "${recipient.scheduler.interval:5}0000")    public MessageDto getMessages() {        MessageDto response = recipientClient.getMessage();        if (response == null) {            log.info("No messages are available at the moment.");            return null;        }        Message message = new Message();        message.setMessage(response.getMessage());        message.setUsername(response.getUser());        messageRepository.save(message);        return response;    }}