package org.pkislov.recipientservice.controller;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.pkislov.recipientservice.dto.MessageDto;import org.pkislov.recipientservice.service.MessageService;import org.springframework.scheduling.annotation.Scheduled;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@Slf4j@RestController@RequestMapping("/message")@RequiredArgsConstructorpublic class MessageController {    private final MessageService messageService;    @Scheduled(fixedRateString = "${recipient.scheduler.interval:5}000")    public void receiveMessages() {        messageService.receiveMessages();    }    @GetMapping    public List<MessageDto> getMessage(){        log.info("Received GET /message");        return messageService.getMessagesAndClean();    }}