package org.pkislov.senderservice.service;import lombok.RequiredArgsConstructor;import org.pkislov.senderservice.dto.MessageDto;import org.springframework.amqp.core.AmqpTemplate;import org.springframework.stereotype.Service;@Service@RequiredArgsConstructorpublic class NotificationService {    public static final String MESSAGE_QUEUE = "messageQueue";    private final NotificationMetricsService notificationMetricsService;    private final AmqpTemplate amqpTemplate;    public MessageDto placeMessageToQueue(MessageDto message) {        amqpTemplate.convertAndSend(MESSAGE_QUEUE, message);        notificationMetricsService.incrementCustomMetric();        return message;    }}