package org.pkislov.visualizerservice.controller;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.pkislov.visualizerservice.dto.MessageDto;import org.pkislov.visualizerservice.service.MessageService;import org.springframework.stereotype.Service;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@Slf4j@Service@RestController@RequiredArgsConstructorpublic class VisualizerController {    private final MessageService messageService;    @GetMapping("/saved-messages")    public List<MessageDto> getSavedMessages() {        List<MessageDto> messages = messageService.getMessages();        log.info("Found {} messages from DB", messages.size());        return messages;    }    @GetMapping    public String get(){        return "Hello World";    }}